import React, { useState } from 'react';
import PropTypes from "prop-types";
import {
  Row,
  Col,
  Card,
  CardBody,
  Form,
  FormGroup,
  FormInput,
  Button,
} from "shards-react";



const EquiPamiento = ({
  onSubmit
  }) => {
    const [equipamiento, setEquipamiento] = useState('');
    const [sala, setSala] = useState('');

    return(
      <Row>
        {/* Editor */}
        <Col lg="9" md="12">
          <Card small className="mb-3">
            <CardBody>
              <Form className="add-new-post">
                <FormGroup>
                  <label>Equipamiento</label>
                  <FormInput
                    value={equipamiento}
                    onChange={(event) => setEquipamiento(event.target.value)}
                    size="lg"
                    className="mb-3"
                    placeholder="Nombre del artÃ­culo" />
                </FormGroup>
                <Form.Group controlId="select-sala">
                <Form.Label>Sala</Form.Label>
                  <Form.Control as="select">
                    <option>1</option>
                    <option>2</option>
                    <option>3</option>
                    <option>4</option>
                    <option>Ninguna</option>
                  </Form.Control>
              </Form.Group>
              </Form>
              <Button
                theme="primary"
                className="mb-2 mr-1"
                onClick={(event) => onSubmit({'equipamiento': equipamiento})}
                // onClick={onSubmit}
                >
                  Agregar
                </Button>
            </CardBody>
          </Card>
        </Col>
      </Row>
    );
  }

Pabellon.propTypes = {
  onSubmit: PropTypes.func,
}

Pabellon.defaultProps = {
  onSubmit: () => {},
}

export default Pabellon;
