import React, { useState } from 'react';
import PropTypes from "prop-types";
import {
  Row,
  Col,
  Card,
  CardBody,
  Form,
  FormGroup,
  FormInput,
  FormSelect,
  Button,
} from "shards-react";

const EquipamientoUpdate = ({
  onSubmit
  }) => {
    const [equipamiento, setEquipamiento] = useState('');
    const [sala, setSala] = useState('');

    return(
      <Row>
        {/* Editor */}
        <Col lg="9" md="12">
          <Card small className="mb-3">
            <CardBody>
              <Form className="add-new-post">
                <FormGroup>
                  <label>Identificador</label>
                  <FormInput
                    value={equipamiento}
                    onChange={(event) => setEquipamiento(event.target.value)}
                    size="lg"
                    className="mb-3"
                    placeholder="Identificador del artÃ­culo" />
                </FormGroup>
                <FormGroup>
                  <label>Sala</label>
                  <FormInput
                    value={sala}
                    onChange={(event) => setSala(event.target.value)}
                    size="lg"
                    className="mb-3"
                    placeholder="Id de Sala" />
                </FormGroup>
              </Form>
              <Button
                theme="primary"
                className="mb-2 mr-1"
                onClick={(event) => onSubmit({'id': equipamiento,'idSala': sala})}
                >
                  Actualizar
                </Button>
            </CardBody>
          </Card>
        </Col>
      </Row>
    );
  }

EquipamientoUpdate.propTypes = {
  onSubmit: PropTypes.func,
}

EquipamientoUpdate.defaultProps = {
  onSubmit: () => {},
}

export default EquipamientoUpdate;
